import React, { useState } from 'react';

const Products = () => {
  const productStyle = {
    textAlign: 'center',
  };

  const imageContainerStyle = {
    display: 'flex',
    flexDirection: 'column',  // Display images in a column
    alignItems: 'center',
  };

  const rowContainerStyle = {
    display: 'flex',
    justifyContent: 'center',
    flexWrap: 'wrap',  // Allow items to wrap to the next line
  };

  const imageStyles = {
    margin: '10px',
    position: 'relative',
    width: '200px', // Set a fixed width for all images
  };

  const discountCircleStyle = {
    position: 'absolute',
    top: '0',
    right: '0',
    backgroundColor: 'red',
    color: 'white',
    borderRadius: '50%',
    padding: '5px',
  };

  const priceContainerStyle = {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
  };

  const mainMRPStyle = {
    textDecoration: 'line-through',
    margin: '5px 0',
  };

  const imageUrls = [
    '/Images.jpg',
    '/image 3.jpg',
    '/image 4.png',
    '/Images.png',
    '/Images5.png',
    '/Images6.png',
    '/Images8.png',
    '/moreImage1.jpg',  // Add additional images
    '/moreImage2.jpg',
    '/moreImage3.jpg',
    '/moreImage4.jpg',
    '/moreImage5.jpg',
    '/moreImage6.jpg',
    '/moreImage7.jpg',
    '/moreImage8.jpg',
    '/moreImage9.jpg',
    '/moreImage10.jpg',
  ];

  const priceStyle = {
    fontWeight: 'bold',
    margin: '5px 0',
  };

  const names = [
    'Product 1',
    'Product 2',
    'Product 3',
    'Product 4',
    'Product 5',
    'Product 6',
    'Product 7',
    'Product 8',
    'Product 9',
    'Product 10',
    'Product 11',
    'Product 12',
    'Product 13',
    'Product 14',
    'Product 15',
    'Product 16',
    'Product 17',
    'Product 18',
  ];

  const initialDisplayCount = 4;  // Initial number of images to display
  const [displayCount, setDisplayCount] = useState(initialDisplayCount);

  const handleShowMore = () => {
    // Increase the display count by 4 when "Show More" is clicked
    setDisplayCount(displayCount + 4);
  };

  const renderProduct = () => {
    return (
      <div style={imageContainerStyle}>
        <div style={rowContainerStyle}>
          {imageUrls.slice(0, displayCount).map((url, index) => (
            <div key={index + 1} style={imageStyles}>
              <div style={discountCircleStyle}>-30%</div>
              <img src={url} alt={`Product ${index + 1}`} style={{ width: '100%' }} />
              <div style={priceContainerStyle}>
                <p style={mainMRPStyle}>${(index + 1) * 10}.00</p>
                <p style={priceStyle}>${(index + 1) * 5}.00</p>
                <p>{names[index]}</p>
              </div>
            </div>
          ))}
        </div>
        {displayCount < imageUrls.length && (
          <button onClick={handleShowMore}>Show More</button>
        )}
      </div>
    );
  };

  return (
    <div style={productStyle}>
      <h1>Our Products</h1>
      {renderProduct()}
    </div>
  );
};

export default Products;
