// FeatureCarousel.js
import React, { useState } from 'react';
import Slider from 'react-slick';
import Resizer from 'react-image-resizer';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';
import './CSS/slick_theme.css';

const FeatureCarousel = () => {
  const [imageSize, setImageSize] = useState({ width: 300, height: 200 }); // Initial image size

  const settings = {
    dots: false,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: true,
    autoplaySpeed: 2000,
    centerMode: true,
    centerPadding: '0',
  };

  const featureImages = [
    '/Rectangle 24.png',
    '/Rectangle 25.png',
    // Add more image URLs as needed
  ];

  const handleImageSizeChange = (width, height) => {
    setImageSize({ width, height });
  };

  return (
    <div className="feature-carousel-container">
      <Slider {...settings}>
        {featureImages.map((image, index) => (
          <div key={index}>
            <Resizer
              src={image}
              width={imageSize.width}
              height={imageSize.height}
              alt={`Feature ${index + 1}`}
            />
          </div>
        ))}
      </Slider>
      <div className="feature-carousel-text">
        <h2>-50+ Beautiful rooms inspiration</h2>
        <p>Our designers have already created a lot of beautiful prototypes of rooms that will inspire you.</p>
        <div>
          <button onClick={() => handleImageSizeChange(300, 200)}>Small Size</button>
          <button onClick={() => handleImageSizeChange(600, 400)}>Medium Size</button>
          <button onClick={() => handleImageSizeChange(900, 600)}>Large Size</button>
        </div>
        <button className="explore-button">Explore More</button>
      </div>
    </div>
  );
};

export default FeatureCarousel;
