import React from 'react';
import { useHistory } from 'react-router-dom';

const HomeSection = () => {
  const rectangleStyle = {
    position: 'absolute',
    width: '643px',
    height: '443px',
    top: '253px',
    left: '739px',
    backgroundColor: 'lightyellow',
    padding: '20px', // Add padding for content inside the rectangle
  };

  const descriptionStyle = {
    margin: 0,
    fontSize: '16px', // Adjust text size
    color: 'black',
    marginBottom: '20px', // Add margin bottom for spacing
  };

  const textStyle = {
    fontSize: '18px', // Adjust text size
    fontWeight: 'bold',
    position: 'absolute',
    width: '561px',
    height: '344px',
    top: '315px',
    left: '778px',
    color: 'black',
  };

  const buyNowStyle = {
    marginTop: '20px', // Adjust the margin as needed
    backgroundColor: '#3498db', // Buy Now container color
    padding: '11px', // Padding for the Buy Now container
    textAlign: 'left', // Align the content to the left
    borderRadius: '5px', // Optional: Add border-radius for rounded corners
    width: '111px', // Adjust the width of the Buy Now container
    position: 'absolute',
    bottom: '20px', // Adjust the bottom position
    left: '20px', // Adjust the left position
  };

  const buyNowTextStyle = {
    margin: 0,
    fontSize: '18px', // Adjust text size
    color: 'white', // Text color for Buy Now
    cursor: 'pointer', // Add cursor pointer on hover
  };

  const handleBuyNowClick = () => {
    // Add your logic for the "BUY NOW" button click here
    console.log('BUY NOW clicked!');
  };

  return (
    <div style={{ position: 'relative' }}>
      <img
        src="/scandinavian-interior-mockup-wall-decal-background 1.png"
        alt="Description of the image"
        style={{ width: '2190px', height: '812.53px' , top:'-75px'}}
      />
      <div style={rectangleStyle}>
        <div style={textStyle}>
          <h4>New Arrival</h4>
          <h1>Discover Our New Collection</h1>
          <p>Lorem ipsum dolor sit amet consectetur adipisicing elit. Quibusdam aliquid veniam ipsam animi ad, facilis dolores quae iusto maxime, quis ducimus dolor voluptatem aperiam cum laboriosam! Dolores beatae quaerat corrupti?</p>
        </div>
        <div style={buyNowStyle} onClick={handleBuyNowClick}>
          <p style={buyNowTextStyle}>BUY NOW</p>
        </div>
      </div>
    </div>
  );
};

export default HomeSection;
