import React from 'react';

const Products = () => {
  const productStyle = {
    textAlign: 'center',
  };

  const imageContainerStyle = {
    display: 'flex',
    justifyContent: 'center',
    flexWrap: 'wrap',
  };

  const imageStyles = {
    margin: '10px',
    position: 'relative',
    width: '200px', // Set a fixed width for all images
    flex: '1 0 30%', // Adjust the flex property to control the number of images in a row
  };

  const discountCircleStyle = {
    position: 'absolute',
    top: '0',
    right: '0',
    backgroundColor: 'red',
    color: 'white',
    borderRadius: '50%',
    padding: '5px',
  };

  const priceContainerStyle = {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
  };

  const mainMRPStyle = {
    textDecoration: 'line-through',
    margin: '5px 0',
  };

  const imageUrls = [
    '/Images.jpg',
    '/image 3.jpg',
    '/image 4.png',
    '/Images.png',
    '/Images5.png',
    '/Images6.png',
    '/Images8.png',
  ];

  const priceStyle = {
    fontWeight: 'bold',
    margin: '5px 0',
  };

  const renderProduct = () => {
    return imageUrls.map((url, index) => (
      <div key={index + 1} style={imageStyles}>
        <div style={discountCircleStyle}>-30%</div>
        <img
          src={url}
          alt={`Product ${index + 1}`}
          style={{ width: '100%' }}
        />
        <div style={priceContainerStyle}>
          <p style={mainMRPStyle}>$70.00</p>
          <p style={priceStyle}>$50.00</p>
        </div>
      </div>
    ));
  };

  return (
    <div style={productStyle}>
      <h1>Our Products</h1>
      <div style={imageContainerStyle}>{renderProduct()}</div>
    </div>
  );
};

export default Products;
