import React, { useState } from 'react';

const Products = () => {
  const productStyle = {
    textAlign: 'center',
  };

  const imageContainerStyle = {
    display: 'flex',
    flexDirection: 'column',  // Display images in a column
    alignItems: 'center',
  };

  const imageStyles = {
    margin: '10px',
    position: 'relative',
    width: '200px', // Set a fixed width for all images
  };

  const discountCircleStyle = {
    position: 'absolute',
    top: '0',
    right: '0',
    backgroundColor: 'red',
    color: 'white',
    borderRadius: '50%',
    padding: '5px',
  };

  const priceContainerStyle = {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
  };

  const mainMRPStyle = {
    textDecoration: 'line-through',
    margin: '5px 0',
  };

  const imageUrls = [
    '/Images.jpg',
    '/image 3.jpg',
    '/image 4.png',
    '/Images.png',
    '/Images5.png',
    '/Images6.png',
    '/Images8.png',
    '/moreImage1.jpg',  // Add additional images
    '/moreImage2.jpg',
    '/moreImage3.jpg',
    '/moreImage4.jpg',
  ];

  const priceStyle = {
    fontWeight: 'bold',
    margin: '5px 0',
  };

  const initialDisplayCount = 7;  // Initial number of images to display
  const [displayCount, setDisplayCount] = useState(initialDisplayCount);

  const handleShowMore = () => {
    // Increase the display count by 4 when "Show More" is clicked
    setDisplayCount(displayCount + 4);
  };

  const renderProduct = () => {
    const renderedImages = [];

    for (let i = 0; i < displayCount; i++) {
      const index = i % imageUrls.length;
      renderedImages.push(
        <div key={i + 1} style={imageStyles}>
          <div style={discountCircleStyle}>-30%</div>
          <img src={imageUrls[index]} alt={`Product ${index + 1}`} style={{ width: '100%' }} />
          <div style={priceContainerStyle}>
            <p style={mainMRPStyle}>$70.00</p>
            <p style={priceStyle}>$50.00</p>
          </div>
        </div>
      );
    }

    return (
      <div style={imageContainerStyle}>
        {renderedImages}
        {displayCount < imageUrls.length && (
          <button onClick={handleShowMore}>Show More</button>
        )}
      </div>
    );
  };

  return (
    <div style={productStyle}>
      <h1>Our Products</h1>
      {renderProduct()}
    </div>
  );
};

export default Products;
