import React from 'react';

const HomeSection = () => {
  const containerStyle = {
    position: 'relative',
    width: '100%',
    height: 'auto',
  };

  const rectangleStyle = {
    position: 'absolute',
    width: '50%',
    height: 'auto',
    top: '50%',
    right: '5%',
    transform: 'translateY(-50%)',
    backgroundColor: 'lightyellow',
    padding: '20px',
    textAlign: 'right', // Align content to the right
  };

  const textStyle = {
    fontSize: '1.5vw',
    fontWeight: 'bold',
    color: 'black',
    textAlign: 'left', // Align text to the left
  };

  const buyNowStyle = {
    marginTop: '20px',
    backgroundColor: '#3498db',
    padding: '11px',
    borderRadius: '5px',
    width: '100px', // Adjust width of the button
    cursor: 'pointer',
    textAlign: 'center', // Align button text to the center
  };

  const handleBuyNowClick = () => {
    console.log('BUY NOW clicked!');
  };

  return (
    <div style={containerStyle}>
      <img
        src="/scandinavian-interior-mockup-wall-decal-background 1.png"
        alt="Description of the image"
        style={{ width: '100%', height: 'auto', position: 'absolute', top: '0', left: '0' }}
      />
      <div style={rectangleStyle}>
        <div style={textStyle}>
          <h4>New Arrival</h4>
          <h1>Discover Our New Collection</h1>
          <p>Lorem ipsum dolor sit amet consectetur adipisicing elit. Quibusdam aliquid veniam ipsam animi ad, facilis dolores quae iusto maxime, quis ducimus dolor voluptatem aperiam cum laboriosam! Dolores beatae quaerat corrupti?</p>
        </div>
        <div style={buyNowStyle} onClick={handleBuyNowClick}>
          <p style={{ margin: 0, fontSize: '18px', color: 'white' }}>BUY NOW</p>
        </div>
      </div>
    </div>
  );
};

export default HomeSection;
