import React from 'react';

const Products = () => {
  const productStyle = {
    textAlign: 'center',
  };

  const imageContainerStyle = {
    display: 'flex',
    justifyContent: 'center',
  };

  const imageStyles = {
    margin: '10px',
    position: 'relative',
  };

  const discountCircleStyle = {
    position: 'absolute',
    top: '0',
    right: '0',
    backgroundColor: 'red',
    color: 'white',
    borderRadius: '50%',
    padding: '5px',
  };

  const priceContainerStyle = {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
  };

  const mainMRPStyle = {
    textDecoration: 'line-through',
    margin: '5px 0',
  };

  const priceStyle = {
    fontWeight: 'bold',
    margin: '5px 0',
  };

  const renderProduct = () => {
    const products = [];
    for (let i = 1; i <= 4; i++) {
      products.push(
        <div key={i} style={imageStyles}>
          <div style={discountCircleStyle}>-30%</div>
          <img
            src={`/Image 5_page-000${i}.jpg`}
            alt={`Tree ${i}`}
            style={{ width: '100%' }}
          />
          <div style={priceContainerStyle}>
            <p style={mainMRPStyle}>$70.00</p>
            <p style={priceStyle}>$50.00</p>
          </div>
          <img
            src={`/Image 5_page-000${i}.jpg`}
            alt={`Tree ${i}`}
            style={{ width: '100%' }}
          />
          
        </div>
        
      );
    }
    return products;
  };

  return (
    <div style={productStyle}>
      <h1>Our Products</h1>
      <div style={imageContainerStyle}>{renderProduct()}</div>
    </div>
  );
};

export default Products;
