import React from 'react';

const HomeSection = () => {
  const rectangleStyle = {
    position: 'absolute',
    bottom: '140px', // Adjust as needed
    right: '300px', // Adjust as needed
    width: '500px', // Adjust as needed
    height: '400px', // Adjust as needed
    backgroundColor: 'lightyellow',
  };
  const descriptionStyle = {
    margin: 0,
    fontSize: '14px', // Adjust text size
    color: 'green', // Optional: Change text color
  };
  const textStyle = {
    // margin: 40,
    // position: 'absolute',
    fontSize: '16px', // Adjust text size
    fontWeight: 'bold',
    Left:'100px',
    bottom: '140px',
    color: 'Black', // Optional: Change text color

  };
  const buyNowStyle = {
    marginTop: '52px', // Adjust the margin as needed
    backgroundColor: '#3498db', // Buy Now container color
    padding: '50px', // Padding for the Buy Now container
    textAlign: 'center', // Center the content
    borderRadius: '5px', // Optional: Add border-radius for rounded corners
    width: '70px', // Adjust the width of the Buy Now container
    position: 'relative', // Enable relative positioning for absolute positioning of the Buy Now text
  };
  const buyNowTextStyle = {
    margin: 0,
    fontSize: '20px', // Adjust text size
    color: 'white', // Text color for Buy Now
    position: 'absolute', // Absolute positioning within the Buy Now container
    top: '100%', // Center vertically within the container
    left: '100%', // Center horizontally within the container
    transform: 'translate(-50%, -50%)', // Center using transform
  };

  return (
    <div style={{ position: 'relative' }}>
      <img
        src="/scandinavian-interior-mockup-wall-decal-background 1.png"
        alt="Description of the image"
        style={{ maxWidth: '100%', maxHeight: '100%', height: 'auto', width: 'auto' }}
      />
      <div style={rectangleStyle}>
        <p style={textStyle}>New Arrival</p>
        <p style={descriptionStyle}>Discover Our New Collection</p>
        <p>Lorem ipsum dolor sit amet consectetur adipisicing elit. Quibusdam aliquid veniam ipsam animi ad, facilis dolores quae iusto maxime, quis ducimus dolor voluptatem aperiam cum laboriosam! Dolores beatae quaerat corrupti?</p>
        <div style={buyNowStyle}>
        <p style={buyNowTextStyle}>Buy Now</p>
      </div>
      </div>
    </div>
  );
};

export default HomeSection;
